import{_ as e,c as o,o as t,ae as r}from"./chunks/framework.Dh1jimFm.js";const l="/27216adf03da8caa82d920ffaa307f89.png",s="/d8088005094a787c8418ced39e0ee0a5.png",d="/83f13ca0d45c7d285740eec0955519d2.png",i="/233745a241e6a26582b204f947dda7c6.png",c="/62a11e68856d51582f44135621d7d8e7.png",n="/d11687dc10a4e4a2f8065b4ef7054e14.png",b=JSON.parse('{"title":"储存配置","description":"","frontmatter":{},"headers":[],"relativePath":"others/storage.md","filePath":"others/storage.md","lastUpdated":1771563132000}'),p={name:"others/storage.md"};function _(m,a,f,g,h,u){return t(),o("div",null,a[0]||(a[0]=[r('<h1 id="储存配置" tabindex="-1">储存配置 <a class="header-anchor" href="#储存配置" aria-label="Permalink to &quot;储存配置&quot;">​</a></h1><p>储存配置主要用于储存各个平台返回的媒体文件，目前储存方式有 3 种，分别是：<strong>本地储存</strong>、<strong>Cloudflare R2 储存</strong>、<strong>阿里云OSS储存</strong>。</p><h3 id="本地储存" tabindex="-1">本地储存 <a class="header-anchor" href="#本地储存" aria-label="Permalink to &quot;本地储存&quot;">​</a></h3><p>本地储存会将媒体文件储存在根目录 <code>public</code> 文件夹下面，可以在后台中设置文件的存活时间以及文件清理周期等。</p><h3 id="cloudflare-r2-储存" tabindex="-1">Cloudflare R2 储存 <a class="header-anchor" href="#cloudflare-r2-储存" aria-label="Permalink to &quot;Cloudflare R2 储存&quot;">​</a></h3><p>Cloudflare R2 储存会将媒体文件储存在 Cloudflare R2 中，可以在后台中设置 Cloudflare R2 配置参数。配置教程如下：</p><ol><li>注册并登陆 <code>https://dash.cloudflare.com</code>，点击左边的 <code>R2对象储存</code>，然后点击右边管理按钮</li></ol><p><img src="'+l+'" alt="image"></p><ol start="2"><li>点击 <code>创建 Account API 令牌</code> 按钮</li></ol><p><img src="'+s+'" alt="image"></p><ol start="3"><li>选择管理员可读可写权限，然后点击创建</li></ol><p><img src="'+d+'" alt="image"></p><ol start="4"><li>然后把这里的值依次填入后台设置中</li></ol><p><img src="'+i+'" alt="image"></p><ol start="5"><li>在储存桶的设置中开启公共开发 URL 或自定义域名，配置好后，填入后台中即可</li></ol><p><img src="'+c+'" alt="image"></p><ol start="6"><li>最终填写示例参考如下</li></ol><p><img src="'+n+'" alt="image"></p><h3 id="阿里云oss储存" tabindex="-1">阿里云OSS储存 <a class="header-anchor" href="#阿里云oss储存" aria-label="Permalink to &quot;阿里云OSS储存&quot;">​</a></h3><p>阿里云OSS储存会将媒体文件储存在阿里云 OSS 中，可以在后台中设置阿里云 OSS 配置参数。</p>',20)]))}const P=e(p,[["render",_]]);export{b as __pageData,P as default};
